fetch("http://localhost:3000/api/products").then((t=>t.json())).then((t=>{for(let n in t){const e=`\n            <a href="./product.html?id=${t[n]._id}">\n                <article>\n                    <img src="${t[n].imageUrl}" alt="${t[n].altTxt}">\n                    <h3 class="productName">${t[n].name}</h3>\n                    <p class="productDescription">${t[n].description}</p>\n                </article>\n            </a>`;document.getElementById("items").innerHTML+=e}})).catch((t=>{console.log(t)}));